import { DialogEx } from "@/registry/components/oui-dialog-ex";
import { Button } from "@/registry/components/ui/oui-button";
import {
  DialogDescription,
  DialogFooter,
  DialogHeader,
} from "@/registry/components/ui/oui-dialog";
import { Heading } from "@/registry/components/ui/oui-heading";

export default function Component() {
  return (
    <DialogEx
      triggerElement={<Button variant="outline">Alert</Button>}
      role="alertdialog"
    >
      <DialogHeader>
        <Heading variant="alert" slot="title">
          Session Expired
        </Heading>
        <DialogDescription>
          Your session has expired due to inactivity. Please log in again to
          continue using the application.
        </DialogDescription>
      </DialogHeader>
      <DialogFooter>
        <Button slot="close" autoFocus>
          OK
        </Button>
      </DialogFooter>
    </DialogEx>
  );
}
