"use client";

import React from "react";
import { FieldError } from "@/registry/default/ui/oui-field";
import { Label } from "@/registry/default/ui/oui-label";
import { RadioGroup } from "@/registry/default/ui/oui-radio-group";
import { Text } from "@/registry/default/ui/oui-text";
import * as Rac from "react-aria-components";

export interface RadioGroupExProps extends Rac.RadioGroupProps {
  label?: React.ReactNode;
  description?: React.ReactNode;
  errorMessage?: string | ((validation: Rac.ValidationResult) => string);
}

export function RadioGroupEx({
  label,
  description,
  errorMessage,
  children,
  ...props
}: RadioGroupExProps) {
  return (
    <RadioGroup {...props}>
      {(renderProps) => (
        <>
          {label && <Label className="">{label}</Label>}
          {description && <Text slot="description">{description}</Text>}
          {typeof children === "function" ? children(renderProps) : children}
          <FieldError>{errorMessage}</FieldError>
        </>
      )}
    </RadioGroup>
  );
}
